import { useEffect, useState } from "react";
import {
  Card,
  CardBody,
  CardTitle,
  ListGroup,
  CardSubtitle,
  ListGroupItem,
  Button,
} from "reactstrap";
import axios from "axios";

const baseUrl= "http://localhost:3002/api/comentarios/"
async function getComentarios (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "GET"
    })
    return response
  } catch (error) {
    console.log(error)
  }
}


const Feeds = () => {
  const [comentarios, setComentarios]= useState([{
    color: "",
    icon: "",
    title: "",
    date: ""
  }]);
  

  useEffect(()=>{
     
  async function loadComentarios(){
    const response= await getComentarios()
   
    setComentarios(response.data.data)
    return response
  }
 

  loadComentarios()
  }, [])
  console.log(comentarios)
  
  return (
    <Card height="300">
      <CardBody height="300">
        <CardTitle tag="h5">Actividades</CardTitle>
        <CardSubtitle className="mb-2 text-muted" tag="h6">
         Empleados
        </CardSubtitle>
        <ListGroup flush className="mt-4" style={{overflowY: "auto", height: "380px"}}>
          {comentarios.map((feed, index) => (
            <ListGroupItem
              key={index}
              action
              href="/"
              tag="a"
              className="d-flex align-items-center p-3 border-0"
            >
              <Button
                className="rounded-circle me-3"
                size="sm"
                color={feed.color}
              >
                <i className={feed.icon}></i>
              </Button>
              {feed.title}
              <small className="ms-auto text-muted text-small">
                {feed.date}
              </small>
            </ListGroupItem>
          ))}
        </ListGroup>
      </CardBody>
    </Card>
  );
};

export default Feeds;
