import axios from "axios";
import { useEffect, useState } from "react";
import { Navigate, useNavigate } from "react-router-dom";
import { Badge, Button, Card, CardBody, CardTitle, Row, Col, Table, CardSubtitle, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input } from "reactstrap";
import { useForm } from "../../hooks/useForm";
import ReactExport from "react-export-excel";




const ExcelFile = ReactExport.ExcelFile;
const ExcelSheet = ReactExport.ExcelFile.ExcelSheet;
const ExcelColumn = ReactExport.ExcelFile.ExcelColumn;



const baseUrl= "http://localhost:3002/api/products/"


async function getProductos (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "GET"
    })
    return response
  } catch (error) {
    console.log(error)
  }
}
async function postProductos (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "POST"
    })
    return <Navigate  to='/'  />
  } catch (error) {
    console.log(error)
  }
}



const ciudadesMasPobladas = [
  {
    ciudad: "Ciudad de Maturin",
    poblacion: 500000,
    entidad: "Maturin",
    pais: "Venezuela"
  },
  {
    ciudad: "Ciudad de Maturin",
    poblacion: 500000,
    entidad: "Maturin",
    pais: "Venezuela"
  },
  {
    ciudad: "Ciudad de Maturin",
    poblacion: 500000,
    entidad: "Maturin",
    pais: "Venezuela"
  },
  {
    ciudad: "Ciudad de Maturin",
    poblacion: 500000,
    entidad: "Maturin",
    pais: "Venezuela"
  }
]

const Badges = () => {
  const  [values, handleInputChange, reset, setValues] = useForm([]);
  const [modalproducto, setModalProducto]=useState(false);
  const navigate= useNavigate();
  console.log(navigate)

  const handleClick=()=>{
    setModalProducto(!modalproducto)
  }
  useEffect(()=>{
     
    async function loadProducts(){
      const response= await postProductos()
      
      setValues(values)
      return response
      
    }
   
  
    loadProducts()
    return <Navigate  to='/'  />
    }, [])
    
    useEffect(()=>{
     
      async function loadProducts(){
        const response= await getProductos()
       
        setValues(response.data.data)
        return response
      }
     
    
      loadProducts()
      }, [])
     


      let redir = () => {
        window.location.href = `/`;
    }
 console.log(values)
  return (
    <div>
      <Card>
        <CardBody>
          <CardTitle tag="h5">Lista de productos</CardTitle>
         <div style={{display: "flex"}}>
           <div>
          <Button
          color="primary"
          onClick={handleClick}
          className="mt-3"
        >
        Agregar Producto
        </Button>
        </div>
        <div style={{ marginTop: "15px"}}>
        <ExcelFile element={<Button color="danger">Exportar a Excel</Button>} filename="Excel">
          <ExcelSheet data={values} name="Lista de productos">
           <ExcelColumn label="id" value="_id"/>
           <ExcelColumn label="Nombre" value="nombre"/>
           <ExcelColumn label="Categoría" value="categoria"/>
           <ExcelColumn label="Precio" value="precio"/>
           <ExcelColumn label="Cantidad" value="cantidad"/>
          </ExcelSheet>
        </ExcelFile>
        </div>
        </div>
        <Modal isOpen={modalproducto} autoFocus={true} centered trapFocus close closeAriaLabel>
    <ModalHeader >
      Modal title
    </ModalHeader>
    <ModalBody>
    <Form  onSubmit={redir} action={baseUrl} method="POST">
             <h6 > --Codigo de cada producto se forma automaticamente-- </h6>
              <FormGroup >
                <Label for="nombre">Nombre del producto</Label>
                <Input
                  id="nombre"
                  name="nombre"
                  placeholder="Nombre"
                  type="text"
                  onChange={handleInputChange}
                />
              </FormGroup>
              <FormGroup >
                <Label for="categoria">Categoría</Label>
                <Input
                  id="categoria"
                  name="categoria"
                  placeholder="categoria"
                  type="text"
                  onChange={handleInputChange}
                />
              </FormGroup>
              <FormGroup >
                <Label for="precio">Precio($)</Label>
                <Input
                  id="precio"
                  name="precio"
                  placeholder="Precio"
                  type="number"
                  onChange={handleInputChange}
                />
              </FormGroup>
              <FormGroup >
                <Label for="cantidad">Cantidad</Label>
                <Input
                  id="cantidad"
                  name="cantidad"
                  placeholder="Cantidad"
                  type="number"
                  onChange={handleInputChange}
                />
              </FormGroup>
             
             
              <ModalFooter>
      <Button
        color="primary"
          
      >
        Agregar 
      </Button>
      {' '}
      <Button onClick={handleClick}>
        Cancel
      </Button>
    </ModalFooter>
              </Form>
    </ModalBody>
   
    
  </Modal>
        <Table bordered className="mt-4">
        <thead>
          <tr>
            <th>
              Codigo
            </th>
            <th>
              Nombre
            </th>
            <th>
              Categoría
            </th>
            <th>
              Precio($)
            </th>
            <th>
            Cantidad
            </th>
          </tr>
        </thead>
        <tbody>
        {Object.values(values).map((item, index) => (
            <tr>
            <th scope="row">
             {item._id}
            </th>
            <td>
            {item.nombre}
            </td>
            <td>
            {item.categoria}
            </td>
            <td>
            {item.precio}
            </td>
            <td>
            {item.cantidad}
            </td>
          </tr>
        ))}
        </tbody>
      </Table>
    
    </CardBody>
    </Card>
     
    </div>
  );
};

export default Badges;
