import { useEffect, useState } from "react";
import axios from "axios";
import { Card, CardBody, CardTitle, CardSubtitle, Table } from "reactstrap";
import user1 from "../../assets/images/users/user1.jpg";
import user2 from "../../assets/images/users/user2.jpg";
import user3 from "../../assets/images/users/user3.jpg";
import user4 from "../../assets/images/users/user4.jpg";
import user5 from "../../assets/images/users/user5.jpg";
import { useFetch } from "../../hooks/useFetch";




const TablaTrabajadores = () => {
  const [products, setProducts]= useState([]);
  const state=useFetch(`http://localhost:3002/api/personas`);
  const {data, loading}= state;
  
  useEffect(()=>{
    setProducts([data])
  })
  return (
    <div>
      <Card>
        <CardBody>
          <CardTitle tag="h5">Lista de trabajadores</CardTitle>
          <CardSubtitle className="mb-2 text-muted" tag="h6">
           Empleados
          </CardSubtitle>

          <Table className="no-wrap mt-3 align-middle" responsive borderless>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Cargo</th>

                <th>Status</th>
                <th>Horas trabajadas</th>
                <th>Salario</th>
              </tr>
            </thead>
            <tbody>
            {products.map((item, index) => (
                <tr key={index} className="border-top">
                  <td>
                    <div className="d-flex align-items-center p-2">
                      <img
                        src={user4}
                        className="rounded-circle"
                        alt="avatar"
                        width="45"
                        height="45"
                      />
                      <div className="ms-3">
                        <h6 className="mb-0">{item._id}</h6>
                        <span className="text-muted">{item.nombre}</span>
                      </div>
                    </div>
                  </td>
                  <td>{item.profesion}</td>
                  <td>
                    {item.activo === false ? (
                      <span className="p-2 bg-danger rounded-circle d-inline-block ms-3"></span>
                    ) : item.activo === false ? (
                      <span className="p-2 bg-warning rounded-circle d-inline-block ms-3"></span>
                    ) : (
                      <span className="p-2 bg-success rounded-circle d-inline-block ms-3"></span>
                    )}
                  </td>
                  <td>{item.horas}</td>
                  <td>{item.salario}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </CardBody>
      </Card>
    </div>
  );
};

export default TablaTrabajadores;
