import React, { useEffect, useState } from "react";
import { Link, Navigate  } from "react-router-dom";
import {
  Navbar,
  Collapse,
  NavbarBrand,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Dropdown,
  Button,
} from "reactstrap";
import Sergio from "../assets/images/users/Sergio.jpg";





const Header = () => {
  const [dolars, setDolars]= useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const  [values, handleInputChange, reset, setValues] = useForm();
  const navigate = useNavigate();
  const baseUrl= "http://localhost:3002/api/user/login"


async function postPersonas (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "POST"
    })
    navigate("/empleo")
  } catch (error) {
    console.log(error)
  }
}
  
  
  useEffect(()=>{

    async function loadProducts(){
      const response= await postPersonas()
      
      setValues(values)
      navigate("/empleo")
      
    }
   
  
    loadProducts()
   
    }, [])
    

  const [dropdownOpen, setDropdownOpen] = useState(false);

  console.log(dolars)
  const toggle = () => setDropdownOpen((prevState) => !prevState);
  const Handletoggle = () => {
    setIsOpen(!isOpen);
  };
  const showMobilemenu = () => {
    document.getElementById("sidebarArea").classList.toggle("showSidebar");
  };
  const cerrarSesion=()=>{
    return <Navigate to='/cerrarsesion'/>
  }
  return (
    <Navbar color="primary" dark expand="md" className="fix-header" style={{position: "fixed", width: "100%", zIndex: 20}}>
      <div className="d-flex align-items-center">
        <div className="d-lg-block d-none me-5 pe-3" style={{color: "white"}}>
          Sistemas distribuidos
        </div>
       
        <Button
          color="primary"
          className=" d-lg-none"
          onClick={() => showMobilemenu()}
        >
          <i className="bi bi-list"></i>
        </Button>
      </div>
      <div className="hstack gap-2">
        <Button
          color="primary"
          size="sm"
          className="d-sm-block d-md-none"
          onClick={Handletoggle}
        >
          {isOpen ? (
            <i className="bi bi-x"></i>
          ) : (
            <i className="bi bi-three-dots-vertical"></i>
          )}
        </Button>
      </div>

      <Collapse navbar isOpen={isOpen}>
       
        <Dropdown isOpen={dropdownOpen} toggle={toggle} style={{marginLeft: "auto", right: 0}}>
          <DropdownToggle color="transparent">
            <img
              src={Sergio}
              alt="profile"
              className="rounded-circle"
              width="30"
            ></img>
          </DropdownToggle>
          <DropdownMenu>
           
            <DropdownItem>Mi Cuenta</DropdownItem>
           
            <DropdownItem divider />
            <DropdownItem onSubmit={reset}>Cerrar Sesión</DropdownItem>
          </DropdownMenu>
        </Dropdown>
      </Collapse>
    </Navbar>
  );
};

export default Header;
