import { useEffect, useState } from "react";
import axios from "axios";
import { Navigate } from "react-router-dom";
import { Badge, Button, Card, CardBody, CardTitle, Row, Col, Table, CardSubtitle, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input } from "reactstrap";
import { useForm } from "../../hooks/useForm";



const baseUrl= "http://localhost:3002/api/proveedores"


async function getProveedor (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "GET"
    })
    return response
  } catch (error) {
    console.log(error)
  }
}
async function postProveedor (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "POST"
    })
    return <Navigate  to='/'  />
  } catch (error) {
    console.log(error)
  }
}
const Grid = () => {
  const  [values, handleInputChange, reset, setValues] = useForm([]);
  const [modalproducto, setModalProducto]=useState(false);

  const handleClick=()=>{
    setModalProducto(!modalproducto)
  }
  useEffect(()=>{
     
    async function loadProveedor(){
      const response= await postProveedor()
      
      setValues(values)
      return response
      
    }
   
  
    loadProveedor()
    return <Navigate  to='/'  />
    }, [])
    
    useEffect(()=>{
     
      async function loadProveedor(){
        const response= await getProveedor()
       
        setValues(response.data.data)
        return response
      }
     
    
      loadProveedor()
      }, [])
     


      let redir = () => {
        window.location.href = `/`;
    }
 console.log(values)
  return (
    <div>
       <Card>
        <CardBody>
          <CardTitle tag="h5">Lista de Proveedores</CardTitle>
         
          <Button
          color="danger"
          onClick={handleClick}
          className="mt-3"
        >
        Agregar Proveedor
        </Button>
      
        <Table bordered className="mt-4">
        <thead>
          <tr>
            <th>
              Codigo
            </th>
            <th>
              Nombre
            </th>
            <th>
              Cédula
            </th>
            <th>
             Dirección
            </th>
            <th>
            Teléfono
            </th>
          </tr>
        </thead>
        <tbody>
        {Object.values(values).map((item, index) => (
            <tr>
            <th scope="row">
             {item._id}
            </th>
            <td>
            {item.nombre}
            </td>
            <td>
            {item.cedula}
            </td>
            <td>
            {item.direccion}
            </td>
            <td>
            {item.telefono}
            </td>
          </tr>
        ))}
        </tbody>
      </Table>
    
    </CardBody>
    </Card>
    <Modal isOpen={modalproducto} autoFocus={true} centered trapFocus close closeAriaLabel>
    <ModalHeader >
      Modal title
    </ModalHeader>
    <ModalBody>
    <Form  onSubmit={redir} action={baseUrl} method="POST">
             <h6 > --Codigo de cada producto se forma automaticamente-- </h6>
              <FormGroup >
                <Label for="nombre">Nombre del Proveedor</Label>
                <Input
                  id="nombre"
                  name="nombre"
                  placeholder="Nombre"
                  type="text"
                  onChange={handleInputChange}
                />
              </FormGroup>
              <FormGroup >
                <Label for="categoria">Cédula</Label>
                <Input
                  id="cedula"
                  name="cedula"
                  placeholder="Cédula"
                  type="text"
                  onChange={handleInputChange}
                />
              </FormGroup>
              <FormGroup >
                <Label for="direccion">Dirección</Label>
                <Input
                  id="direccion"
                  name="direccion"
                  placeholder="Dirección"
                  type="text"
                  onChange={handleInputChange}
                />
              </FormGroup>
              <FormGroup >
                <Label for="telefono">Teléfono</Label>
                <Input
                  id="telefono"
                  name="telefono"
                  placeholder="Teléfono"
                  type="number"
                  onChange={handleInputChange}
                />
              </FormGroup>
             
             
              <ModalFooter>
      <Button
        color="primary"
          
      >
        Agregar 
      </Button>
      {' '}
      <Button onClick={handleClick}>
        Cancel
      </Button>
    </ModalFooter>
              </Form>
    </ModalBody>
   
    
  </Modal>
    </div>
  );
};

export default Grid;
