import {
  Card,
  Row,
  Col,
  CardTitle,
  CardBody,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
} from "reactstrap";

import { Link } from "react-router-dom";
import { useForm } from "./hooks/useForm";
import axios from 'axios';
import { Navigate  } from "react-router-dom";
import { useEffect } from "react";
import { Alert } from "bootstrap";
import {useHistory} from "react-router";


  

const Login = () => {
  const  [values, handleInputChange, reset, setValues] = useForm();
  const {push} = useHistory();
  const baseUrl= "http://localhost:3002/api/user/login"

async function postPersonas (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "POST"
    })
    return <Navigate  to='/empleo'  />
  } catch (error) {
    console.log(error)
  }
}
  
  
  useEffect(()=>{

    async function loadProducts(){
      const response= await postPersonas()
      
      setValues(values)
      return <Navigate  to='/empleo'  />
      
    }
   
  
    loadProducts()
    return <Navigate  to='/empleo'  />
    }, [])
         
const base= <Alert color="primary">This is a primary alert — check it out!</Alert>
        
         
  return (
      <div>
      <Card style={{width: "500px", marginLeft: "auto", marginRight: "auto", justifyContent: "center", marginTop: "50px"}}>
          <CardTitle tag="h4" className=" p-3 " style={{textAlign: "center"}}>
           
            Iniciar Sesión
          </CardTitle>
          <CardBody>
          <Form  action={baseUrl} method="POST">
              <FormGroup >
                <Label for="email">Email</Label>
                <Input
                  id="email"
                  name="email"
                  
                  type="text"
                  required
                />
              </FormGroup>
              <FormGroup >
                <Label for="exampleEmail">Contraseña</Label>
                <Input
                  id="password"
                  name="password"
                  type="password"
                  required
                />
              </FormGroup>
             <Button type="submit" color="primary" style={{width: "100%"}}>Ingresar</Button>
            </Form>
            <div style={{marginTop: "20px"}}><Link to="/registro">Registrarse</Link></div>

          </CardBody>
        </Card>
      </div>
      
  );
};

export default Login;
