import { useEffect, useState } from "react";
import axios from "axios";
import { Navigate, useNavigate } from "react-router-dom";
import { Badge, Button, Card, CardBody, CardTitle, Row, Col, Table, CardSubtitle, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input } from "reactstrap";
import { useForm } from "../../hooks/useForm";



const baseUrl= "http://localhost:3002/api/products/"


async function getProductos (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "GET"
    })
    return response
  } catch (error) {
    console.log(error)
  }
}
async function postProductos (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "POST"
    })
    return <Navigate  to='/'  />
  } catch (error) {
    console.log(error)
  }
}


const Badges = () => {
  const  [values, handleInputChange, reset, setValues] = useForm([]);
  const [modalproducto, setModalProducto]=useState(false);
  const navigate= useNavigate();
  console.log(navigate)

  const handleClick=()=>{
    setModalProducto(!modalproducto)
  }
  useEffect(()=>{
     
    async function loadProducts(){
      const response= await postProductos()
      
      setValues(values)
      return response
      
    }
   
  
    loadProducts()
    return <Navigate  to='/'  />
    }, [])
    
    useEffect(()=>{
     
      async function loadProducts(){
        const response= await getProductos()
       
        setValues(response.data.data)
        return response
      }
     
    
      loadProducts()
      }, [])
     


      let redir = () => {
        window.location.href = `/`;
    }
 console.log(values)
  return (
    <div>
      <Card>
        <CardBody>
          <CardTitle tag="h5">Lista de productos</CardTitle>
         <div style={{display: "flex"}}>
           <div>
          <Button
          color="primary"
          onClick={handleClick}
          className="mt-3"
        >
        Agregar Producto
        </Button>
        </div>
       
        </div>
        <Modal isOpen={modalproducto} autoFocus={true} centered trapFocus close closeAriaLabel>
    <ModalHeader >
      Modal title
    </ModalHeader>
    <ModalBody>
    <Form  onSubmit={redir} action={baseUrl} method="POST">
             <h6 > --Codigo de cada producto se forma automaticamente-- </h6>
              <FormGroup >
                <Label for="nombre">Nombre del producto</Label>
                <Input
                  id="nombre"
                  name="nombre"
                  placeholder="Nombre"
                  type="text"
                  onChange={handleInputChange}
                />
              </FormGroup>
              <FormGroup >
                <Label for="categoria">Categoría</Label>
                <Input
                  id="categoria"
                  name="categoria"
                  placeholder="categoria"
                  type="text"
                  onChange={handleInputChange}
                />
              </FormGroup>
              <FormGroup >
                <Label for="precio">Precio($)</Label>
                <Input
                  id="precio"
                  name="precio"
                  placeholder="Precio"
                  type="number"
                  onChange={handleInputChange}
                />
              </FormGroup>
              <FormGroup >
                <Label for="cantidad">Cantidad</Label>
                <Input
                  id="cantidad"
                  name="cantidad"
                  placeholder="Cantidad"
                  type="number"
                  onChange={handleInputChange}
                />
              </FormGroup>
             
             
              <ModalFooter>
      <Button
        color="primary"
          
      >
        Agregar 
      </Button>
      {' '}
      <Button onClick={handleClick}>
        Cancel
      </Button>
    </ModalFooter>
              </Form>
    </ModalBody>
   
    
  </Modal>
        <Table className="no-wrap mt-3 align-middle" responsive border>
        <thead>
          <tr>
            <th>
              Codigo
            </th>
            <th>
              Nombre
            </th>
            <th>
              Categoría
            </th>
            <th>
              Precio($)
            </th>
            <th>
            Cantidad
            </th>
          </tr>
        </thead>
        <tbody>
       
        </tbody>
      </Table>
    
    </CardBody>
    </Card>
     
    </div>
  );
};

export default Badges;
