import {
  Card,
  Row,
  Col,
  CardTitle,
  CardBody,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
} from "reactstrap";

import { Link } from "react-router-dom";
import { useForm } from "./hooks/useForm";
import axios from 'axios';

import { useEffect } from "react";
import { Alert } from "bootstrap";
import { useNavigate } from 'react-router-dom';


  

const Login = () => {
  const  [values, handleInputChange, reset, setValues] = useForm();
  const navigate = useNavigate();
  const baseUrl= "http://localhost:3002/api/user/login"


async function postPersonas (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "POST"
    })
   
  } catch (error) {
    console.log(error)
  }
}
  
  
  useEffect(()=>{

    async function loadProducts(){
      const response= await postPersonas()
      
      setValues(values)
     
    }
    loadProducts()
   
    }, [])
    
 const handleLogin=()=>{
  navigate("/empleo", {
    replace:true
  })
 }
      
        
         
  return (
      <div>
      <Card style={{width: "500px", marginLeft: "auto", marginRight: "auto", justifyContent: "center", marginTop: "50px"}}>
          <CardTitle tag="h4" className=" p-3 " style={{textAlign: "center"}}>
           
            Iniciar Sesi√≥n
          </CardTitle>
          <CardBody>
          <Form action={baseUrl}  method="POST">
              <FormGroup >
                <Label for="email">Email</Label>
                <Input
                  id="email"
                  name="email"
                  
                  type="text"
                  required
                />
              </FormGroup>
              <FormGroup >
                <Label for="exampleEmail">Contrase√±a</Label>
                <Input
                  id="password"
                  name="password"
                  type="password"
                  required
                />
              </FormGroup>
            <Button color="primary" style={{width: "100%"}} onClick={handleLogin}> Ingresar</Button>
            </Form>
            <Link to="/registro"> <div style={{marginTop: "20px"}}>Registrarse</div></Link>

          </CardBody>
        </Card>
      </div>
      
  );
};

export default Login;
