import React, { useEffect, useState } from "react";
import {
  Alert,
  UncontrolledAlert,
  Card,
  CardBody,
  CardTitle,
  CardText,
  Button,
  Row,
  Col,
  Form,
} from "reactstrap";
import axios from "axios";
import { useForm } from "../../hooks/useForm";
import { Navigate  } from "react-router-dom";


const baseUrl= "http://localhost:3002/api/proyectospublicados"
const baseUrlSuscripciones= "http://localhost:3002/api/proyectospublicados"


async function postProyectos (){
  try {
    const response = await axios({
     url: `${baseUrlSuscripciones}`,
     method: "POST"
    })
    return <Navigate  to='/#/alerts'  />
  } catch (error) {
    console.log(error)
  }
}



async function getProductos (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "GET"
    })
    return response
  } catch (error) {
    console.log(error)
  }
}
const Alerts = () => {
  const [suscrito, setSuscrito] = useState(false);
  const  [values, handleInputChange, reset, setValues] = useForm([]);
  
  useEffect(()=>{
     
    async function loadProducts(){
      const response= await getProductos()
     
      setValues(response.data.data)
      return response
    }
   
  
    loadProducts()
    }, [])
      
    useEffect(()=>{
     
      async function loadProyectos(){
        const response= await postProyectos()
        
        setValues(values)
        return response
        
      }
     
    
      loadProyectos()
      return <Navigate  to='/#/alerts'  />
      }, [])
    const handleSubmit=()=>{
      setSuscrito(!suscrito)
    }
   
    

    console.log(values)

    
  return (
    <div>
    
      <Card>
     
        <CardTitle tag="h3" className="border-bottom p-3 mb-0">
          <i className="bi bi-bell me-2"> </i>
         Empleos publicados por la comunidad
        </CardTitle>
      </Card>
      <Row style={{height: "500px"}}>

      
         {values.length>0 ? values.map((item, index) => (
           <Form  action={baseUrlSuscripciones} method="POST">
          <Col md="6" lg="6" key={item._id}>
         <Card body style={{minHeight: "315px"}}>
         <CardTitle tag="h3" value={item.tema}  id="tema" name="tema" onChange={handleInputChange}>{item.tema}</CardTitle>
         <CardText value={item.contenido} name="contenido" onChange={handleInputChange}>
         {item.contenido}
         </CardText>
         <CardText value={item.topicos} name="topicos" onChange={handleInputChange}>
         {item.topicos}
         </CardText>
         <CardText>
          {item.nombreempleador}
         </CardText>
         <div>
         
           <Button type="submit" color={!suscrito ?  "primary" : "info"} name="view" onClick={handleSubmit} >{!suscrito  ? "Suscribirse" : "Suscrito"}</Button>
          
         </div>
       </Card>
       <div  style={{marginBottom: "auto", top: 88, position: "fixed", marginLeft: "auto", right: 0}}>
      {suscrito ? <div style={{marginLeft: "auto", right: 0}}><Alert color="primary">Te has suscrito a este empleo</Alert></div> : ""}
      </div>
       </Col>
       </Form>
      
     )) :
     <CardTitle tag="h4" className="border-bottom p-3 mb-0" style={{textAlign: "center"}}>
        
         ¡No hay registros de proyectos!
        </CardTitle>
       } 
      

       
 
      </Row>
    

    </div>
  );
};

export default Alerts;
