import React, { useEffect, useState } from "react";
import axios from "axios";
import { useForm } from "../../hooks/useForm"
import {
  Card,
  Row,
  Col,
  CardTitle,
  CardBody,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  CardText,
 
} from "reactstrap";

const baseUrl= "http://localhost:3002/api/proyectospublicados"
const baseUrlSuscripciones= "http://localhost:3002/api/proyectospublicados"


async function getProductos (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "GET"
    })
    return response
  } catch (error) {
    console.log(error)
  }
}
const Suscripciones = () => {
  const [suscrito, setSuscrito] = useState(false);
  const  [values, handleInputChange, reset, setValues] = useForm([]);
  
  useEffect(()=>{
     
    async function loadProducts(){
      const response= await getProductos()
     
      setValues(response.data.data)
      return response
    }
   
  
    loadProducts()
    }, [])
   
    const suscribirse=()=>{
      setSuscrito(!suscrito)
    }

    console.log(values)

    
  return (
    <div>
    
      <Card>
        <CardTitle tag="h3" className="border-bottom p-3 mb-0">
          <i className="bi bi-bell me-2"> </i>
         Mis suscripciones
        </CardTitle>
      </Card>
      <Row style={{height: "500px"}}>
         {values.length>0 ? values.map((item, index) => (
          <Col md="6" lg="6" key={item._id}>
             <Form  action={baseUrlSuscripciones} method="POST" >
              <FormGroup >
                <Label for="tema">Tema del proyecto</Label>
                <Input
                  id="tema"
                  name="tema"
                  type="text"
                  onChange={handleInputChange}
                  value={item.tema}
                  required
                />
              </FormGroup>
              <FormGroup >
                <Label for="contenido">Contenido</Label>
                <Input
                  id="contenido"
                  name="contenido"
                  type="text"
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>
              <FormGroup >
                <Label for="topicos">Topicos</Label>
                <Input
                  id="topicos"
                  name="topicos"
                  type="text"
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>
          
              <Button type="submit">Enviar</Button>
            </Form>

         <Card body style={{minHeight: "315px"}}>
         <CardTitle tag="h3">{item.tema}</CardTitle>
         <CardText>
         {item.contenido}
         </CardText>
         <CardText>
         {item.topicos}
         </CardText>
         <CardText style={{display: "flex"}}>
         <div> Publicado por</div> <div style={{color: "black", marginLeft: "10px"}}>{item.nombreempleador}</div>
         </CardText>
        
         <div>
           <Button color="warning" id={item._id} onClick={suscribirse}>Eliminar</Button>
          
         </div>
       </Card>
       </Col>
      
     )) :
     <CardTitle tag="h4" className="border-bottom p-3 mb-0" style={{textAlign: "center"}}>
        
         ¡No hay registros de suscripciones!
        </CardTitle>

        
       } 
      </Row>
        

    </div>
  );
};

export default Suscripciones;
