import React, { useEffect, useState } from "react";
import {
  Alert,
  UncontrolledAlert,
  Card,
  CardBody,
  CardTitle,
  CardText,
  Button,
  Row,
  Col,
} from "reactstrap";
import axios from "axios";
import { useForm } from "../../hooks/useForm";


const baseUrl= "http://localhost:3002/api/proyectospublicados"


async function getProductos (){
  try {
    const response = await axios({
     url: `${baseUrl}`,
     method: "GET"
    })
    return response
  } catch (error) {
    console.log(error)
  }
}
const Alerts = () => {
  const [suscrito, setSuscrito] = useState(false);
  const  [values, handleInputChange, reset, setValues] = useForm([]);
  
  useEffect(()=>{
     
    async function loadProducts(){
      const response= await getProductos()
     
      setValues(response.data.data)
      return response
    }
   
  
    loadProducts()
    }, [])
   
    const suscribirse=()=>{
      setSuscrito(!suscrito)
    }

    console.log(values)

    
  return (
    <div>
    
      <Card>
        <CardTitle tag="h6" className="border-bottom p-3 mb-0">
          <i className="bi bi-bell me-2"> </i>
         Empleos publicados por la comunidad
        </CardTitle>
       
       
      </Card>
      <Row style={{height: "500px"}}>
       
       
       
         {values.length>0 ? values.map((item, index) => (
          <Col md="6" lg="6" key={item._id}>
         <Card body style={{minHeight: "315px"}}>
         <CardTitle tag="h3">{item.tema}</CardTitle>
         <CardText>
         {item.contenido}
         </CardText>
         <CardText>
         {item.topicos}
         </CardText>
         <CardText>
         {item.nombreempleador}
         </CardText>
         <div>
           <Button color={!suscrito && item._id ?  "primary" : "info"} id={item._id} onClick={suscribirse}>{!suscrito && item._id ? "Suscribirse" : "Suscrito"}</Button>
          
         </div>
       </Card>
       </Col>
      
     )) :
     <div style={{textAlign: "center"}}>No hay registros</div>

        
       } 

       
         
        
        
       
       
      </Row>
        

    </div>
  );
};

export default Alerts;
