import { Button, Nav, NavItem } from "reactstrap";
import { Link, useLocation } from "react-router-dom";
import Sergio from "../assets/images/users/Sergio.jpg";


const navigation = [
  {
    title: "Inicio",
    href: "/dashboard",
    icon: "bi bi-speedometer2",
  },
  {
    title: "Categorias",
    href: "/alerts",
    icon: "bi bi-bell",
  },
  {
    title: "Inventario",
    href: "/badges",
    icon: "bi bi-patch-check",
  },
  {
    title: "Clientes",
    href: "/buttons",
    icon: "bi bi-hdd-stack",
  },
 
  {
    title: "Proveedores",
    href: "/grid",
    icon: "bi bi-card-text",
  },

  {
    title: "Empleados",
    href: "/table",
    icon: "bi bi-layout-split",
  },
  
  {
    title: "Reportes",
    href: "/forms",
    icon: "bi bi-textarea-resize",
  },
  {
    title: "Crear Factura",
    href: "/cards",
    icon: "bi bi-card-text",
  },
  {
    title: "Configuraciones",
    href: "/breadcrumbs",
    icon: "bi bi-link",
  },
  {
    title: "Manual de uso",
    href: "/about",
    icon: "bi bi-people",
  },
];

const Sidebar = () => {
  const showMobilemenu = () => {
    document.getElementById("sidebarArea").classList.toggle("showSidebar");
  };
  let location = useLocation();
 
  return (
    <div >
      <div className="d-flex align-items-center"></div>
      <div
        className="profilebg"
       
      >
        <div className="p-3 d-flex">
          <img src={Sergio} alt="user" width="120"  className="rounded-circle" style={{marginLeft: "auto", marginRight: "auto"}} />
          <Button
            color="white"
            className="ms-auto text-white d-lg-none"
            onClick={() => showMobilemenu()}
          >
            <i className="bi bi-x"></i>
          </Button>
        </div>
        <div className="bg-dark text-white p-2 opacity-75">Sergio Herrera</div>
      </div>
      <div className="p-3 mt-2">
        <Nav vertical className="sidebarNav">
          {navigation.map((navi, index) => (
            <NavItem key={index} className="sidenav-bg">
              <Link
                to={navi.href}
                className={
                  location.pathname === navi.href
                    ? "active nav-link py-3"
                    : "nav-link text-secondary py-3"
                }
              >
                <i className={navi.icon}></i>
                <span className="ms-3 d-inline-block">{navi.title}</span>
              </Link>
            </NavItem>
          ))}
       
        </Nav>
      </div>
    </div>
  );
};

export default Sidebar;
